    private static String leftPad(String org, Integer length, char padChar) {
        StringBuilder orgBuilder = new StringBuilder(org);
        while (orgBuilder.length() < length) {
            orgBuilder.append(padChar);
        }
        return orgBuilder.toString();
    }

    public static String generate(boolean mobilePhone, int withStar) {
        if (randomAB(0, 100) < withStar) {
            String phoneWithoutStar = genPre(mobilePhone) + leftPad("" + randomAB(0, 99999999 + 1), 8, '0');
            return phoneWithoutStar.substring(0, 3) + "****" + phoneWithoutStar.substring(phoneWithoutStar.length()-4);
        } else {
            return genPre(mobilePhone) + leftPad("" + randomAB(0, 99999999 + 1), 8, '0');
        }
    }


    /*
     * 随机返回a和b其中一个数
     */
    public static int randomAB(int a, int b) {
        return (int) ((Math.random() * Math.abs(a - b)) + Math.min(a, b));
    }

    private static String genPre(boolean mobilePhone) {
        if (mobilePhone) {
            return "" + MOBILE_PREFIX[randomAB(0, MOBILE_PREFIX.length)];
        } else {
            return "" + TELEPHONE_PREFIX[randomAB(0, TELEPHONE_PREFIX.length)];
        }
    }

